import React, { useState } from 'react';
import { debounce } from 'lodash';

interface EditorProps {
  onMarkdownChange: (markdown: string) => void;
}

const Editor: React.FC<EditorProps> = ({ onMarkdownChange }) => {
  const [markdown, setMarkdown] = useState<string>('');

  const handleMarkdownChange = debounce((event: React.ChangeEvent<HTMLTextAreaElement>) => {
    const value = event.target.value;
    setMarkdown(value);
    onMarkdownChange(value); // Send the Markdown to the parent component
  }, 300); // Debounce the input to prevent too many updates

  return (
    <div className="editor-container">
      <textarea
        value={markdown}
        onChange={handleMarkdownChange}
        placeholder="Enter your Markdown..."
      />
    </div>
  );
};

export default Editor;
