import { useEffect, useState } from 'react';

const useWebSocket = () => {
  const [html, setHtml] = useState<string>('');
  const ws = new WebSocket('ws://localhost:5000'); // Using native WebSocket

  useEffect(() => {
    // When a message is received from the server, update the HTML preview
    ws.onmessage = (event: MessageEvent) => {
      setHtml(event.data); // The received message is the rendered HTML
    };

    // Clean up the connection on unmount
    return () => {
      ws.close();
    };
  }, []);

  const sendMarkdown = (markdown: string) => {
    // Send the markdown text to the server via WebSocket
    if (ws.readyState === WebSocket.OPEN) {
      ws.send(markdown);
    }
  };

  return { html, sendMarkdown };
};

export default useWebSocket;
